mapper.select:
	select
	userid,username,truename,phone,anumber,role,status,logindate,registerdate,balance
	from t_user
	
		and role = 0
    
      and USERNAME like '%'||#{username}||'%'
    
    
      and PHONE = #{phone}
    
    
      and ANUMBER = #{anumber,jdbcType=VARCHAR}
    
    
      and STATUS = #{status,jdbcType=DECIMAL}
    
    
      and TRUENAME = #{truename,jdbcType=VARCHAR}
    
    
      and LOGINDATE = #{logindate,jdbcType=TIMESTAMP}
    
    
      and REGISTERDATE = #{registerdate}
    
    
      and balance = #{balance,jdbcType=DECIMAL}

  	select
  	count(1)
  	from t_user
  	
  		
        and USERNAME = #{username,jdbcType=VARCHAR}
      
	     
	        or PHONE = #{phone,jdbcType=VARCHAR}

  	select
  	
  	from t_user
  	
  		
        	and USERNAME = #{username,jdbcType=VARCHAR}
        	and PASSWORD = #{password,jdbcType=VARCHAR}
        	and ROLE = 0
      	
	    
	        and PHONE = #{phone,jdbcType=VARCHAR}
	        and PASSWORD = #{password,jdbcType=VARCHAR}
	        and ROLE = 0

  	select
  	
  	from t_user
  	
  		
        	and USERNAME = #{username,jdbcType=VARCHAR}
        	and PASSWORD = #{password,jdbcType=VARCHAR}
        	and ROLE = 1
      	
	    
	        and PHONE = #{phone,jdbcType=VARCHAR}
	        and PASSWORD = #{password,jdbcType=VARCHAR}
	        and ROLE = 1

select count(1)
from t_user
where userid = #{userid}
and password = #{password}

select count(1)
from t_user
where username = #{username}
and userid != #{userid}

select count(1)
from t_user
where phone = #{phone}
and userid != #{userid}

select count(1)
from t_user
where userid = #{userid}

mapper.update:
update t_user
set status = 1
where userid = #{userid}

